input
|> String.split("\n", trim: true)
|> Enum.with_index(fn r, i -> to_charlist(r) |> Enum.with_index(fn c, j -> {{i, j}, c} end) end)
|> then(fn m ->
  n = Enum.zip_with(m, & &1)
  g = for r <- n, Enum.all?(r, &elem(&1, 1) == ?.), do: hd(r) |> elem(0) |> elem(1)
  h = for r <- m, Enum.all?(r, &elem(&1, 1) == ?.), do: hd(r) |> elem(0) |> elem(0)
  p = for r <- m, {{i, j}, ?#} <- r, do: {i, j}
  d = fn a, b, c -> abs(a - b) + Enum.count(c, &min(a, b) <= &1 and &1 <= max(a, b)) end
  for {i1, j1}=a <- p, {i2, j2}=b <- p, a < b, do: d.(i1, i2, h) + d.(j1, j2, g)
end)
|> Enum.sum()
|> IO.inspect(label: :part1, charlists: :as_lists)

input
|> String.split("\n", trim: true)
|> Enum.with_index(fn r, i -> to_charlist(r) |> Enum.with_index(fn c, j -> {{i, j}, c} end) end)
|> then(fn m ->
  n = Enum.zip_with(m, & &1)
  g = for r <- n, Enum.all?(r, &elem(&1, 1) == ?.), do: hd(r) |> elem(0) |> elem(1)
  h = for r <- m, Enum.all?(r, &elem(&1, 1) == ?.), do: hd(r) |> elem(0) |> elem(0)
  p = for r <- m, {{i, j}, ?#} <- r, do: {i, j}
  d = fn a, b, c -> abs(a - b) + 999999 * Enum.count(c, &min(a, b) <= &1 and &1 <= max(a, b)) end
  for {i1, j1}=a <- p, {i2, j2}=b <- p, a < b, do: d.(i1, i2, h) + d.(j1, j2, g)
end)
|> Enum.sum()
|> IO.inspect(label: :part2, charlists: :as_lists)

